// Cart Data
let cart = [];
let currentFilter = "all";

// Initialize the page
document.addEventListener("DOMContentLoaded", function () {
  loadProducts();
  updateCartCount();

  // Smooth scrolling for navigation links
  document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
    anchor.addEventListener("click", function (e) {
      e.preventDefault();
      const target = document.querySelector(this.getAttribute("href"));
      if (target) {
        target.scrollIntoView({
          behavior: "smooth",
          block: "start",
        });
      }
    });
  });
});

// Load products to the page
async function loadProducts(filter = "all") {
  const productsGrid = document.getElementById("productsGrid");

  try {
    // Fetch products from API
    const response = await fetch("http://localhost:3000/api/products");
    if (!response.ok) {
      throw new Error("Failed to fetch products");
    }

    const apiProducts = await response.json();

    // Convert API products to match the expected format
    const products = apiProducts.map((product) => ({
      id: product._id,
      name: product.name,
      category: product.category,
      price: product.price,
      description: product.description || "",
      image:
        product.image || "https://via.placeholder.com/300x200?text=No+Image",
      stock: product.stock || 0,
    }));

    const filteredProducts =
      filter === "all"
        ? products
        : products.filter((product) => product.category === filter);

    productsGrid.innerHTML = "";

    filteredProducts.forEach((product) => {
      const productCard = createProductCard(product);
      productsGrid.appendChild(productCard);
    });
  } catch (error) {
    console.error(
      "Error loading products from API, using local products:",
      error
    );

    // Fallback to local products array
    const filteredProducts =
      filter === "all"
        ? products
        : products.filter((product) => product.category === filter);

    productsGrid.innerHTML = "";

    filteredProducts.forEach((product) => {
      const productCard = createProductCard(product);
      productsGrid.appendChild(productCard);
    });
  }
}

// Create product card element
function createProductCard(product) {
  const card = document.createElement("div");
  card.className = "product-card";
  card.innerHTML = `
        <div class="product-image" onclick="goToProductDetail(${
          product.id
        })" style="cursor: pointer;">
            <img src="${product.image}" alt="${product.name}" loading="lazy">
        </div>
        <div class="product-info">
            <h3 class="product-title" onclick="goToProductDetail(${
              product.id
            })" style="cursor: pointer;">${product.name}</h3>
            <p class="product-description">${product.description}</p>
            <div class="product-price">${formatPrice(product.price)} تومان</div>
            <button class="add-to-cart-btn" onclick="event.stopPropagation(); addToCart(${
              product.id
            })">
                افزودن به سبد خرید
            </button>
        </div>
    `;
  return card;
}

// Go to product detail page
function goToProductDetail(productId) {
  window.location.href = `product-detail.html?id=${productId}`;
}

// Filter products by category
function filterProducts(category) {
  currentFilter = category;
  loadProducts(category);

  // Update filter button states
  document.querySelectorAll(".filter-btn").forEach((btn) => {
    btn.classList.remove("active");
  });
  event.target.classList.add("active");
}

// Add product to cart
async function addToCart(productId) {
  try {
    // Fetch the specific product from API
    const response = await fetch(
      `http://localhost:3000/api/products/${productId}`
    );
    if (!response.ok) {
      throw new Error("Failed to fetch product");
    }

    const apiProduct = await response.json();
    const product = {
      id: apiProduct._id,
      name: apiProduct.name,
      category: apiProduct.category,
      price: apiProduct.price,
      description: apiProduct.description || "",
      image:
        apiProduct.image || "https://via.placeholder.com/300x200?text=No+Image",
      stock: apiProduct.stock || 0,
    };

    const existingItem = cart.find((item) => item.id === productId);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      cart.push({
        ...product,
        quantity: 1,
      });
    }

    updateCartCount();
    updateCartDisplay();
    showNotification("محصول به سبد خرید اضافه شد");
  } catch (error) {
    console.error("Error adding to cart:", error);
    showNotification("خطا در افزودن به سبد خرید", "error");
  }
}

// Remove product from cart
function removeFromCart(productId) {
  cart = cart.filter((item) => item.id !== productId);
  updateCartCount();
  updateCartDisplay();
  showNotification("محصول از سبد خرید حذف شد");
}

// Update product quantity in cart
function updateQuantity(productId, change) {
  const item = cart.find((item) => item.id === productId);
  if (item) {
    item.quantity += change;
    if (item.quantity <= 0) {
      removeFromCart(productId);
    } else {
      updateCartCount();
      updateCartDisplay();
    }
  }
}

// Update cart count display
function updateCartCount() {
  const cartCount = document.querySelector(".cart-count");
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
  cartCount.textContent = totalItems;
}

// Update cart display
function updateCartDisplay() {
  const cartItems = document.getElementById("cartItems");
  const cartTotal = document.getElementById("cartTotal");

  cartItems.innerHTML = "";

  if (cart.length === 0) {
    cartItems.innerHTML =
      '<p style="text-align: center; color: #666; padding: 2rem;">سبد خرید خالی است</p>';
    cartTotal.textContent = "0 تومان";
    return;
  }

  cart.forEach((item) => {
    const cartItem = document.createElement("div");
    cartItem.className = "cart-item";
    cartItem.innerHTML = `
            <div class="cart-item-image">
                <img src="${item.image}" alt="${item.name}" loading="lazy">
            </div>
            <div class="cart-item-info">
                <div class="cart-item-title">${item.name}</div>
                <div class="cart-item-price">${formatPrice(
                  item.price
                )} تومان</div>
                <div class="cart-item-quantity">
                    <button class="quantity-btn" onclick="updateQuantity(${
                      item.id
                    }, -1)">-</button>
                    <span>${item.quantity}</span>
                    <button class="quantity-btn" onclick="updateQuantity(${
                      item.id
                    }, 1)">+</button>
                </div>
            </div>
            <button class="remove-item" onclick="removeFromCart(${
              item.id
            })">حذف</button>
        `;
    cartItems.appendChild(cartItem);
  });

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  cartTotal.textContent = formatPrice(total) + " تومان";
}

// Toggle cart sidebar
function toggleCart() {
  const cartSidebar = document.getElementById("cartSidebar");
  cartSidebar.classList.toggle("open");
}

// Checkout function
function checkout() {
  if (cart.length === 0) {
    showNotification("سبد خرید خالی است", "error");
    return;
  }

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  showNotification("در حال انتقال به صفحه پرداخت...", "info");

  // Here you would typically redirect to a payment gateway
  setTimeout(() => {
    showNotification("پرداخت با موفقیت انجام شد!", "success");
    cart = [];
    updateCartCount();
    updateCartDisplay();
    toggleCart();
  }, 2000);
}

// Scroll to products section
function scrollToProducts() {
  const productsSection = document.getElementById("products");
  productsSection.scrollIntoView({
    behavior: "smooth",
    block: "start",
  });
}

// Format price with commas
function formatPrice(price) {
  return price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

// Show notification
function showNotification(message, type = "success") {
  // Remove existing notifications
  const existingNotifications = document.querySelectorAll(".notification");
  existingNotifications.forEach((notification) => {
    notification.remove();
  });

  const notification = document.createElement("div");
  notification.className = `notification ${type}`;
  notification.innerHTML = `
        <div class="notification-content">
            <span>${message}</span>
            <button onclick="this.parentElement.parentElement.remove()">×</button>
        </div>
    `;

  document.body.appendChild(notification);

  // Auto remove after 3 seconds
  setTimeout(() => {
    if (notification.parentElement) {
      notification.remove();
    }
  }, 3000);
}

// Show loading animation
function showLoading() {
  const loading = document.createElement("div");
  loading.className = "loading";
  loading.innerHTML = `
        <div class="loading-spinner"></div>
        <p>در حال بارگذاری...</p>
    `;
  document.body.appendChild(loading);
}

// Hide loading animation
function hideLoading() {
  const loading = document.querySelector(".loading");
  if (loading) {
    loading.remove();
  }
}

// Search products
function searchProducts(query) {
  const filteredProducts = products.filter(
    (product) =>
      product.name.toLowerCase().includes(query.toLowerCase()) ||
      product.description.toLowerCase().includes(query.toLowerCase())
  );

  const productsGrid = document.getElementById("productsGrid");
  productsGrid.innerHTML = "";

  if (filteredProducts.length === 0) {
    productsGrid.innerHTML =
      '<p style="text-align: center; color: #666; padding: 2rem;">محصولی یافت نشد</p>';
    return;
  }

  filteredProducts.forEach((product) => {
    const productCard = createProductCard(product);
    productsGrid.appendChild(productCard);
  });
}

// Add keyboard shortcuts
document.addEventListener("keydown", function (e) {
  // Ctrl/Cmd + K to open search
  if ((e.ctrlKey || e.metaKey) && e.key === "k") {
    e.preventDefault();
    // You can add a search modal here
    showNotification("جستجو (Ctrl+K)", "info");
  }

  // Escape to close cart
  if (e.key === "Escape") {
    const cartSidebar = document.getElementById("cartSidebar");
    if (cartSidebar.classList.contains("open")) {
      cartSidebar.classList.remove("open");
    }
  }
});

// Add product comparison functionality
let compareList = [];

function addToCompare(productId) {
  const product = products.find((p) => p.id === productId);
  if (compareList.length < 3 && !compareList.find((p) => p.id === productId)) {
    compareList.push(product);
    showNotification("محصول به لیست مقایسه اضافه شد");
  } else if (compareList.find((p) => p.id === productId)) {
    showNotification("این محصول قبلاً در لیست مقایسه است", "error");
  } else {
    showNotification("حداکثر ۳ محصول قابل مقایسه است", "error");
  }
}

// Save cart to localStorage
function saveCart() {
  localStorage.setItem("cart", JSON.stringify(cart));
}

// Load cart from localStorage
function loadCart() {
  const savedCart = localStorage.getItem("cart");
  if (savedCart) {
    cart = JSON.parse(savedCart);
    updateCartCount();
    updateCartDisplay();
  }
}

// Auto-save cart when it changes
function updateCart() {
  updateCartCount();
  updateCartDisplay();
  saveCart();
}

// Load cart on page load
document.addEventListener("DOMContentLoaded", function () {
  loadCart();
  // Load products on page load
  loadProducts();
});

// Update the addToCart function to use the new updateCart
function addToCart(productId) {
  const product = products.find((p) => p.id === productId);
  const existingItem = cart.find((item) => item.id === productId);

  if (existingItem) {
    existingItem.quantity += 1;
  } else {
    cart.push({
      ...product,
      quantity: 1,
    });
  }

  updateCart();
  showNotification("محصول به سبد خرید اضافه شد");
}
